#include <stdio.h>

int main() {
    long long n, m;
    scanf("%lld %lld", &n, &m);
    long long expected_time = 0;
    long long success_prob = 1;
    long long time_per_submission = 100 * (n - m) + 1900 * m;
    long long num_submissions = 0;

    while (success_prob > 1e-15) {
        num_submissions++;
        success_prob *= 0.5;
        expected_time += (long long)(time_per_submission * num_submissions * success_prob);

    }

    printf("%lld\n", expected_time);

    return 0;
}

#include <iostream>

using namespace std;

int main() {
  long long n, m;
  cin >> n >> m;

  long long expected_time = 0;
  long long success_prob = 1;
  long long time_per_submission = 100 * (n - m) + 1900 * m;

  for (long long i = 1; ; ++i) {
    expected_time += (time_per_submission * i) * success_prob;
    if (m == 0) break;
    success_prob *= (long double)1/2;
    if (success_prob == 0) break;
    if (i >= 10000) break;
  }

  cout << expected_time << endl;
  return 0;
}

import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        long ans = 0;
        long prob = 1;
        for (int i = 1; ; i++) {
            ans += (long) i * (long) (Math.pow(2, m)) * (long) (n - m) * 100 + (long) i * (long) (Math.pow(2, m)) * m * 1900;
            prob *= 2;
            if (Math.pow(2, m) >= prob) break;

        }
        System.out.println(ans);
    }
}

#include <stdio.h>
#include <stdlib.h>

int main() {
    int N, Z, W;
    scanf("%d %d %d", &N, &Z, &W);
    int a[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &a[i]);
    }
    long long max_diff = 0;
    for (int i = 1; i < (1 << N); i++) {
        long long current_x = Z;
        long long current_y = W;
        int x_turn = 1;
        int cards_left = N;
        int current_index = 0;
        while (cards_left > 0) {
            int num_cards = 0;
            int next_index = current_index;
            int best_card = -1;
            for (int j = 0; j < cards_left; j++) {
                if ((i >> (current_index + j)) & 1) {
                    num_cards++;
                    best_card = a[current_index + j];
                } else {
                    break;
                }
            }
            if (x_turn) {
                current_x = best_card;
            } else {
                current_y = best_card;
            }
            current_index += num_cards;
            cards_left -= num_cards;
            x_turn = 1 - x_turn;

        }
        max_diff = (max_diff < abs(current_x - current_y)) ? abs(current_x - current_y) : max_diff;
    }
    printf("%lld\n", max_diff);
    return 0;
}

