using namespace std;

int main() {
  long long n, z, w;
  cin >> n >> z >> w;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long max_score = 0;

  function<void(int, long long, long long, bool)> solve = 
    [&](int idx, long long cur_z, long long cur_w, bool x_turn) {
    if (idx == n) {
      max_score = max(max_score, abs(cur_z - cur_w));
      return;
    }

    if (x_turn) {
      for (int i = idx; i < n; ++i) {
        solve(i + 1, a[i], cur_w, false);
      }
    } else {
      for (int i = idx; i < n; ++i) {
        solve(i + 1, cur_z, a[i], true);
      }
    }
  };

  solve(0, z, w, true);
  cout << max_score << endl;
  return 0;
}

import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long z = sc.nextLong();
        long w = sc.nextLong();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long[][] dp = new long[n + 1][2];
        dp[n][0] = Math.abs(z - w);
        dp[n][1] = Math.abs(z - w);
        for (int i = n - 1; i >= 0; i--) {
            long max = 0;
            for (int j = i; j < n; j++) {
                long min = Long.MAX_VALUE;
                for (int k = j + 1; k < n; k++) {
                    min = Math.min(min, dp[k][1]);
                }
                max = Math.max(max, Math.abs(a[j] - w) - min);
            }
            dp[i][0] = max;
            long min = Long.MAX_VALUE;
            for (int j = i; j < n; j++) {
                long max1 = 0;
                for (int k = j + 1; k < n; k++) {
                    max1 = Math.max(max1, dp[k][0]);
                }
                min = Math.min(min, Math.abs(a[j] - z) + max1);
            }
            dp[i][1] = min;
        }
        System.out.println(dp[0][0]);
    }
}

def solve():
    n, z, w = map(int, input().split())
    a = list(map(int, input().split()))

    def get_score(x_card, y_card):
        return abs(x_card - y_card)

    def minimax(cards, turn, x_card, y_card):
        if not cards:
            return get_score(x_card, y_card)

        if turn == 'X':
            best_score = -1
            for i in range(1, len(cards) + 1):
                new_x_card = cards[i - 1]
                score = minimax(cards[i:], 'Y', new_x_card, y_card)
                best_score = max(best_score, score) if best_score != -1 else score
            return best_score
        else:
            best_score = float('inf')
            for i in range(1, len(cards) + 1):
                new_y_card = cards[i - 1]
                score = minimax(cards[i:], 'X', x_card, new_y_card)
                best_score = min(best_score, score)
            return best_score

    print(minimax(a, 'X', z, w))

