int main() {
    int N, Q;
    scanf("%d", &N);
    int b[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &b[i]);
    }
    scanf("%d", &Q);
    int l[Q], r[Q];
    for (int i = 0; i < Q; i++) {
        scanf("%d %d", &l[i], &r[i]);
        l[i]--; 
        r[i]--; 
    }
    int min_hamming = N + 1;
    for (int i = 0; i < (1 << Q); i++) {
        int a[N];
        for (int j = 0; j < N; j++) {
            a[j] = 0;
        }
        for (int j = 0; j < Q; j++) {
            if ((i >> j) & 1) {
                for (int k = l[j]; k <= r[j]; k++) {
                    a[k] = 1;
                }
            }
        }
        int hamming = 0;
        for (int j = 0; j < N; j++) {
            if (a[j] != b[j]) {
                hamming++;
            }
        }
        if (hamming < min_hamming) {
            min_hamming = hamming;
        }
    }
    printf("%d\n", min_hamming);
    return 0;
}

