using namespace std;

int main() {
  long long k;
  cin >> k;

  queue<pair<long long, long long>> q;
  q.push({0, 0});

  map<long long, long long> visited;
  visited[0] = 0;

  while (!q.empty()) {
    long long num = q.front().first;
    long long sum = q.front().second;
    q.pop();

    if (num % k == 0 && num != 0) {
      cout << sum << endl;
      return 0;
    }

    for (int i = 0; i <= 9; ++i) {
      long long next_num = num * 10 + i;
      long long next_sum = sum + i;

      if (visited.find(next_num % k) == visited.end() || visited[next_num % k] > next_sum) {
        visited[next_num % k] = next_sum;
        q.push({next_num, next_sum});
      }
    }
  }
  return 0;
}

