typedef unsigned long long ull;

ull binToDec(const char *bin) {
    ull dec = 0;
    ull power = 1;
    for (int i = strlen(bin) - 1; i >= 0; i--) {
        if (bin[i] == '1') {
            dec += power;
        }
        power *= 2;
    }
    return dec;
}

int main() {
    int n;
    char x_str[4001];
    scanf("%d %s", &n, x_str);

    ull x = binToDec(x_str);
    ull a[n];
    for (int i = 0; i < n; i++) {
        char a_str[4001];
        scanf("%s", a_str);
        a[i] = binToDec(a_str);
    }

    ull reachable[x + 1];
    memset(reachable, 0, sizeof(reachable));

    for (int i = 0; i < n; i++) {
        reachable[a[i]] = 1;
    }

    for (ull i = 0; i <= x; i++) {
        if (reachable[i]) {
            if (i * 2 <= x) reachable[i * 2] = 1;
            for (int j = 0; j < n; j++) {
                if (i ^ a[j] <= x) reachable[i ^ a[j]] = 1;
            }
        }
    }

    ull count = 0;
    for (ull i = 0; i <= x; i++) {
        if (reachable[i]) count++;
    }

    printf("%llu\n", count);
    return 0;
}

