        BigInteger x = new BigInteger(sc.next(), 2);
        Set<BigInteger> s = new HashSet<>();
        for (int i = 0; i < n; i++) {
            s.add(new BigInteger(sc.next(), 2));
        }
        Set<BigInteger> res = new HashSet<>(s);
        Queue<BigInteger> q = new LinkedList<>(s);
        while (!q.isEmpty()) {
            BigInteger cur = q.poll();
            BigInteger doubled = cur.multiply(BigInteger.valueOf(2));
            if (doubled.compareTo(x) <= 0 && !res.contains(doubled)) {
                res.add(doubled);
                q.offer(doubled);
            }
            for (BigInteger val : res) {
                BigInteger xor = cur.xor(val);
                if (xor.compareTo(x) <= 0 && !res.contains(xor)) {
                    res.add(xor);
                    q.offer(xor);
                }
            }
        }
        System.out.println(res.size());
    }
}

