    for (int i = 0; i < (1 << N); i++) {
        double cur = 0;
        double cur_v = 0;
        double cur_t = 0;
        for (int j = 0; j < N; j++) {
            double accel_t = (double)v[j] - cur_v;
            double decel_t = cur_v;
            double maintain_t = t[j];
            if (accel_t > t[j]) {
                accel_t = t[j];
                maintain_t = 0;
            } else {
                maintain_t -= accel_t;
            }
            if (decel_t > t[j]) {
                decel_t = t[j];
                maintain_t = 0;
            } else {
                maintain_t -= decel_t;

