  cin >> n >> m;

  vector<tuple<int, int>> edges;
  for (int i = 0; i < m; ++i) {
    int u, v;
    cin >> u >> v;
    edges.emplace_back(u, v);
  }

  vector<vector<int>> adj(n + 1);
  for (const auto& edge : edges) {
    adj[get<0>(edge)].push_back(get<1>(edge));
    adj[get<1>(edge)].push_back(get<0>(edge));
  }

  int count = 0;
  vector<vector<bool>> added_edges(n + 1, vector<bool>(n + 1, false));
  for (int i = 1; i <= n; ++i) {
    for (int j = 1; j <= n; ++j) {
      if (i == j) continue;
      if (added_edges[i][j]) continue;

      vector<int> q;
      q.push_back(i);
      vector<int> dist(n + 1, -1);
      dist[i] = 0;
      int head = 0;

      while (head < q.size()) {
        int u = q[head++];
        if (dist[u] == 3 && u == j) {
          count++;
          added_edges[i][j] = added_edges[j][i] = true;
          break;
        }
        if (dist[u] > 3) break;
        for (int v : adj[u]) {
          if (dist[v] == -1) {
            dist[v] = dist[u] + 1;
            q.push_back(v);
          }
        }
      }
    }
  }
