import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<List<Integer>> graph = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            graph.add(new ArrayList<>());
        }
        for (int i = 0; i < m; i++) {
            int u = sc.nextInt() - 1;
            int v = sc.nextInt() - 1;
            graph.get(u).add(v);
            graph.get(v).add(u);
        }
        Set<Pair> addedEdges = new HashSet<>();
        int count = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (i == j) continue;
                Queue<Pair> q = new LinkedList<>();
                q.offer(new Pair(i, 0));
                Set<Integer> visited = new HashSet<>();
                visited.add(i);
                boolean found = false;
                while (!q.isEmpty()) {
                    Pair p = q.poll();
                    if (p.node == j && p.dist == 3) {
                        found = true;
                        break;
                    }
                    if (p.dist >= 3) continue;
                    for (int neighbor : graph.get(p.node)) {
                        if (!visited.contains(neighbor)) {
                            visited.add(neighbor);
                            q.offer(new Pair(neighbor, p.dist + 1));
                        }
                    }
                }
                if (found && !addedEdges.contains(new Pair(i, j)) && !addedEdges.contains(new Pair(j,i))) {
                    addedEdges.add(new Pair(i, j));
                    count++;
                }
            }
        }
        System.out.println(count);
        sc.close();
    }

    static class Pair {
        int node;
        int dist;

        Pair(int node, int dist) {
            this.node = node;
            this.dist = dist;
        }

        @Override
        public boolean equals(Object obj) {
            if (this == obj) return true;
            if (obj == null || getClass() != obj.getClass()) return false;
            Pair pair = (Pair) obj;
            return node == pair.node && dist == pair.dist;
        }
        @Override
        public int hashCode(){
            return Objects.hash(node,dist);
        }
    }
}

