#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long a, b;
  cin >> a >> b;

  long long mod = 1e9 + 7;

  if (a == 0 && b == 0) {
    cout << 1 << endl;
    return 0;
  }

  if (a == 0 || b == 0) {
    cout << 1 << endl;
    return 0;
  }

  vector<long long> fact(a + b + 1);
  fact[0] = 1;
  for (long long i = 1; i <= a + b; ++i) {
    fact[i] = (fact[i - 1] * i) % mod;
  }

  auto power = [&](long long base, long long exp) {
    long long res = 1;
    base %= mod;
    while (exp > 0) {
      if (exp % 2 == 1) res = (res * base) % mod;
      base = (base * base) % mod;
      exp /= 2;
    }
    return res;
  };

  auto inv = [&](long long n) {
    return power(n, mod - 2);
  };

  auto C = [&](long long n, long long k) {
    if (k < 0 || k > n) return 0LL;
    return fact[n] * inv(fact[k]) % mod * inv(fact[n - k]) % mod;
  };

  cout << C(a + b, a) << endl;
  return 0;
}

