#include <stdio.h>
#include <stdlib.h>

int main() {
    int N;
    scanf("%d", &N);
    double A[N], B[N], C[N];
    for (int i = 0; i < N; i++) {
        scanf("%lf %lf %lf", &A[i], &B[i], &C[i]);
    }
    double x_coords[N * (N - 1) / 2];
    double y_coords[N * (N - 1) / 2];
    int k = 0;
    for (int i = 0; i < N; i++) {
        for (int j = i + 1; j < N; j++) {
            double det = A[i] * B[j] - A[j] * B[i];
            x_coords[k] = (B[i] * C[j] - B[j] * C[i]) / det;
            y_coords[k] = (A[j] * C[i] - A[i] * C[j]) / det;
            k++;
        }
    }
    double median_x = 0, median_y = 0;
    if (k > 0) {
        double temp_x[k], temp_y[k];
        for (int i = 0; i < k; i++) {
            temp_x[i] = x_coords[i];
            temp_y[i] = y_coords[i];
        }
        qsort(temp_x, k, sizeof(double), [](const void *a, const void *b) {
            return (*(double *)a > *(double *)b) - (*(double *)a < *(double *)b);
        });
        qsort(temp_y, k, sizeof(double), [](const void *a, const void *b) {
            return (*(double *)a > *(double *)b) - (*(double *)a < *(double *)b);
        });
        if (k % 2 == 0) {
            median_x = (temp_x[k / 2 - 1] + temp_x[k / 2]) / 2;
            median_y = (temp_y[k / 2 - 1] + temp_y[k / 2]) / 2;
        } else {
            median_x = temp_x[k / 2];
            median_y = temp_y[k / 2];
        }
    }
    printf("%.15lf %.15lf\n", median_x, median_y);
    return 0;
}

import java.util.*;
import java.lang.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        double[][] lines = new double[n][3];
        for (int i = 0; i < n; i++) {
            lines[i][0] = sc.nextDouble();
            lines[i][1] = sc.nextDouble();
            lines[i][2] = sc.nextDouble();
        }
        List<double[]> points = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                double a1 = lines[i][0];
                double b1 = lines[i][1];
                double c1 = lines[i][2];
                double a2 = lines[j][0];
                double b2 = lines[j][1];
                double c2 = lines[j][2];
                double det = a1 * b2 - a2 * b1;
                double x = (b1 * c2 - b2 * c1) / det;
                double y = (a2 * c1 - a1 * c2) / det;
                points.add(new double[]{x, y});
            }
        }
        double minX = Double.MAX_VALUE;
        double minY = Double.MAX_VALUE;
        double minSumDist = Double.MAX_VALUE;
        for (double x = -100; x <= 100; x += 0.000001) {
            for (double y = -100; y <= 100; y += 0.000001) {
                double sumDist = 0;
                for (double[] point : points) {
                    sumDist += Math.abs(point[0] - x) + Math.abs(point[1] - y);
                }
                if (sumDist < minSumDist) {
                    minSumDist = sumDist;
                    minX = x;
                    minY = y;
                } else if (sumDist == minSumDist) {
                    if (x < minX) {
                        minX = x;
                        minY = y;
                    } else if (x == minX && y < minY) {
                        minY = y;
                    }
                }
            }
        }
        System.out.printf("%.15f %.15f\n", minX, minY);
    }
}

#include <stdio.h>

long long power(long long base, long long exp, long long mod) {
    long long res = 1;
    base %= mod;
    while (exp > 0) {
        if (exp % 2 == 1) res = (res * base) % mod;
        base = (base * base) % mod;
        exp /= 2;
    }
    return res;
}

