        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                a[i][j] = sc.nextLong();
            }
        }
        long ans = 0;
        List<long[]> edges = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                edges.add(new long[]{i, j, a[i][j]});
            }
        }
        Collections.sort(edges, (x, y) -> Long.compare(x[2], y[2]));
        long[] d = new long[n];
        Arrays.fill(d, Long.MAX_VALUE);
        d[0] = 0;
        for (long[] e : edges) {
            int u = (int) e[0];
            int v = (int) e[1];
            long w = e[2];
            if (d[u] != Long.MAX_VALUE && d[v] > d[u] + w) {
                d[v] = d[u] + w;
                ans += w;
            } else if (d[v] != Long.MAX_VALUE && d[u] > d[v] + w) {
                d[u] = d[v] + w;
                ans += w;
            }
        }
        boolean ok = true;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (a[i][j] != Long.MAX_VALUE) {
                    long dist = Long.MAX_VALUE;
                    Queue<Integer> q = new LinkedList<>();
                    q.offer(i);
                    long[] dists = new long[n];
                    Arrays.fill(dists, Long.MAX_VALUE);
                    dists[i] = 0;
                    while (!q.isEmpty()) {
                        int u = q.poll();
                        for (long[] e : edges) {
                            int v = -1;
                            if (e[0] == u) v = (int) e[1];
                            if (e[1] == u) v = (int) e[0];
                            if (v != -1 && dists[v] > dists[u] + e[2]) {
                                dists[v] = dists[u] + e[2];
                                q.offer(v);
                            }
                        }
                    }
                    if (dists[j] != a[i][j]) ok = false;
                }
            }
        }
        if (ok) System.out.println(ans);
        else System.out.println(-1);
    }
}

