public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<List<Integer>> adj = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            adj.add(new ArrayList<>());
        }
        for (int i = 1; i < n; i++) {
            int p = sc.nextInt() - 1;
            adj.get(p).add(i);
        }
        int[] x = new int[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextInt();
        }
        if (solve(0, adj, x)) {
            System.out.println("POSSIBLE");
        } else {
            System.out.println("IMPOSSIBLE");
        }
    }

    static boolean solve(int u, List<List<Integer>> adj, int[] x) {
        if (u == adj.size()) {
            return true;
        }
        for (int i = 0; i < 2; i++) {
            int[] w = new int[adj.size()];
            if (dfs(u, adj, x, w, i)) {
                return true;
            }
        }
        return false;
    }

    static boolean dfs(int u, List<List<Integer>> adj, int[] x, int[] w, int color) {
        w[u] = x[u];
        if (w[u] < 0) return false;
        for (int v : adj.get(u)) {
            if(!dfs(v, adj, x, w, 1-color)){
                return false;
            }
            w[u] -= w[v];
        }
        if (w[u] < 0) return false;
        return true;
    }
}

