int main() {
    int n;
    scanf("%d", &n);
    int x[2 * n], y[2 * n];
    for (int i = 0; i < 2 * n; i++) {
        scanf("%d %d", &x[i], &y[i]);
    }
    long long ans = 0;
    int *p = (int *)malloc(sizeof(int) * 2 * n);
    for (int i = 0; i < 2 * n; i++) {
        p[i] = i;
    }
    do {
        int a_used[n + 1] = {0}, b_used[n + 1] = {0};
        int flag = 1;
        for (int i = 0; i < 2 * n; i++) {
            int idx = p[i];
            if (idx < n) {
                int a = idx + 1;
                int min_y = -1;
                for (int j = 0; j < 2 * n; j++) {
                    if (x[j] == a && !a_used[j]) {
                        if (min_y == -1 || y[j] < min_y) {
                            min_y = y[j];
                        }
                    }
                }
                if (min_y == -1) {
                    flag = 0;
                    break;
                }
                for (int j = 0; j < 2 * n; j++) {
                    if (x[j] == a && y[j] == min_y) {
                        a_used[j] = 1;
                        break;
                    }
                }
            } else {
                int b = idx - n + 1;
                int min_x = -1;
                for (int j = 0; j < 2 * n; j++) {
                    if (y[j] == b && !b_used[j]) {
                        if (min_x == -1 || x[j] < min_x) {
                            min_x = x[j];
                        }
                    }
                }
                if (min_x == -1) {
                    flag = 0;
                    break;
                }
                for (int j = 0; j < 2 * n; j++) {
                    if (x[j] == min_x && y[j] == b) {
                        b_used[j] = 1;
                        break;
                    }
                }
            }
        }
        if (flag) {
            ans++;
        }
    } while (next_permutation(p, p + 2 * n));
    printf("%lld\n", ans % MOD);
    free(p);
    return 0;
}

