    vector<int> towns(r);
    for (int i = 0; i < r; ++i) {
        cin >> towns[i];
        towns[i]--; 
    }

    vector<tuple<int, int, int>> edges;
    for (int i = 0; i < m; ++i) {
        int u, v, w;
        cin >> u >> v >> w;
        edges.emplace_back(u - 1, v - 1, w);
    }

    long long min_dist = numeric_limits<long long>::max();
    sort(towns.begin(), towns.end());

    do {
        long long current_dist = 0;
        for (size_t i = 0; i < towns.size() - 1; ++i) {
            int start_node = towns[i];
            int end_node = towns[i + 1];

            vector<long long> dist(n, numeric_limits<long long>::max());
            dist[start_node] = 0;

            for (int j = 0; j < n - 1; ++j) {
                for (const auto& edge : edges) {
                    int u, v, w;
                    tie(u, v, w) = edge;
                    if (dist[u] != numeric_limits<long long>::max() && dist[u] + w < dist[v]) {
                        dist[v] = dist[u] + w;
                    }
                    if (dist[v] != numeric_limits<long long>::max() && dist[v] + w < dist[u]) {
                        dist[u] = dist[v] + w;
                    }
                }
            }
            current_dist += dist[end_node];
        }
        min_dist = min(min_dist, current_dist);
    } while (next_permutation(towns.begin(), towns.end()));

    cout << min_dist << endl;

    return 0;
}

