    }

    static boolean nextPermutation(int[] p) {
        int n = p.length;
        int i = n - 2;
        while (i >= 0 && p[i] >= p[i + 1]) i--;
        if (i < 0) return false;
        int j = n - 1;
        while (p[j] <= p[i]) j--;
        swap(p, i, j);
        reverse(p, i + 1, n - 1);
        return true;
    }

    static void swap(int[] p, int i, int j) {
        int temp = p[i];
        p[i] = p[j];
        p[j] = temp;
    }

    static void reverse(int[] p, int i, int j) {
        while (i < j) {
            swap(p, i, j);
            i++;
            j--;
        }
    }
}

