def pow_mod(x, y, p):
    res = 1
    x = x % p
    while y > 0:
        if y & 1:
            res = (res * x) % p
        y = y >> 1
        x = (x * x) % p
    return res

def inv_mod(x, p):
    return pow_mod(x, p - 2, p)

N, M = map(int, input().split())

if N == M:
    ans = (N + M) * inv_mod(2, MOD) % MOD
else:
