    def solve(n, m):
        if (n, m) in dp:
            return dp[(n, m)]
        if n == 0:
            return 0
        if m == 0:
            return n

        res = (solve(n - 1, m) * m + solve(n, m - 1) * n) * inv_mod(n + m, MOD) % MOD
        res = (res + 1) % MOD
        dp[(n, m)] = res
        return res

    ans = solve(N, M)

print(ans)

