class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        String s1 = sc.next();
        String s2 = sc.next();
        Map<Character, Integer> dominoMap = new HashMap<>();
        int count = 0;
        for (int i = 0; i < n; i++) {
            char c1 = s1.charAt(i);
            char c2 = s2.charAt(i);
            if (!dominoMap.containsKey(c1)) {
                dominoMap.put(c1, count++);
            }
            if (!dominoMap.containsKey(c2)) {
                dominoMap.put(c2, count++);
            }
        }
        int numDominoes = dominoMap.size();
        long[][] dp = new long[n + 1][3];
        dp[0][0] = 1;
        dp[0][1] = 1;
        dp[0][2] = 1;
        long mod = 1000000007;
        for (int i = 1; i <= n; i++) {
            char c1 = s1.charAt(i - 1);
            char c2 = s2.charAt(i - 1);
            int dominoIndex = dominoMap.get(c1);
            if (c1 != c2) {
                dominoIndex = dominoMap.get(c2);
            }
            for (int j = 0; j < 3; j++) {
                for (int k = 0; k < 3; k++) {
                    if (j != k) {
                        dp[i][j] = (dp[i][j] + dp[i - 1][k]) % mod;
                    }
                }
            }
        }
        long ans = (dp[n][0] + dp[n][1] + dp[n][2]) % mod;
        System.out.println(ans);
    }
}

MOD = 1000000007

def solve():
    n = int(input())
    s1 = input()
    s2 = input()

    adj = {}
    for i in range(n):
        if s1[i] not in adj:
            adj[s1[i]] = []
        if s2[i] not in adj:
            adj[s2[i]] = []
        if i > 0:
            if s1[i] != s1[i-1]:
                adj[s1[i]].append(s1[i-1])
                adj[s1[i-1]].append(s1[i])
            if s2[i] != s2[i-1]:
                adj[s2[i]].append(s2[i-1])
                adj[s2[i-1]].append(s2[i])
            if s1[i] != s2[i-1]:
                adj[s1[i]].append(s2[i-1])
                adj[s2[i-1]].append(s1[i])
            if s2[i] != s1[i-1]:
                adj[s2[i]].append(s1[i-1])
                adj[s1[i-1]].append(s2[i])


    dominoes = set(list(s1) + list(s2))

    color_count = {}
    for d in dominoes:
        color_count[d] = 3


    count = 1
    for d in dominoes:
        count = (count * color_count[d]) % MOD

    print(count)

