def solve():
    a = input()

    def is_subsequence(s, t):
        i = 0
        j = 0
        while i < len(s) and j < len(t):
            if s[i] == t[j]:
                i += 1
            j += 1
        return i == len(s)

    for length in range(1, 10):
        for s in generate_strings(length):
            if not is_subsequence(s, a):
                print(s)
                return

def generate_strings(length):
    if length == 0:
        yield ""
        return
    for char_code in range(ord('a'), ord('z') + 1):
        for rest in generate_strings(length - 1):
            yield chr(char_code) + rest

