        edges[i][0]--; 
        edges[i][1]--; 
    }

    int q, k;
    scanf("%d %d", &q, &k);
    k--; 

    for (int i = 0; i < q; i++) {
        int x, y;
        scanf("%d %d", &x, &y);
        x--; 
        y--; 

        ll dist_x_k = INF;
        ll dist_y_k = INF;

        ll dist[n];
        for (int j = 0; j < n; j++) dist[j] = INF;
        dist[x] = 0;

        for (int j = 0; j < n -1; j++) {
            for (int l = 0; l < n - 1; l++) {
                if (dist[edges[l][0]] != INF && dist[edges[l][1]] > dist[edges[l][0]] + edges[l][2]) {
                    dist[edges[l][1]] = dist[edges[l][0]] + edges[l][2];
                }
                if (dist[edges[l][1]] != INF && dist[edges[l][0]] > dist[edges[l][1]] + edges[l][2]) {
                    dist[edges[l][0]] = dist[edges[l][1]] + edges[l][2];
                }
            }
        }
        dist_x_k = dist[k];

        for (int j = 0; j < n; j++) dist[j] = INF;
        dist[y] = 0;
        for (int j = 0; j < n -1; j++) {
            for (int l = 0; l < n - 1; l++) {
                if (dist[edges[l][0]] != INF && dist[edges[l][1]] > dist[edges[l][0]] + edges[l][2]) {
                    dist[edges[l][1]] = dist[edges[l][0]] + edges[l][2];
                }
                if (dist[edges[l][1]] != INF && dist[edges[l][0]] > dist[edges[l][1]] + edges[l][2]) {
                    dist[edges[l][0]] = dist[edges[l][1]] + edges[l][2];
                }
            }
        }
        dist_y_k = dist[k];

        if (dist_x_k == INF || dist_y_k == INF) {
            printf("-1\n");
        } else {
            printf("%lld\n", dist_x_k + dist_y_k);
        }
    }

    return 0;
}

import java.util.*;

public class Main {
    static final int INF = Integer.MAX_VALUE;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<List<Pair>> adj = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            adj.add(new ArrayList<>());
        }
        for (int i = 0; i < n - 1; i++) {
            int u = sc.nextInt() - 1;
            int v = sc.nextInt() - 1;
            int w = sc.nextInt();
            adj.get(u).add(new Pair(v, w));
            adj.get(v).add(new Pair(u, w));
        }
        int q = sc.nextInt();
        int k = sc.nextInt() - 1;
        for (int i = 0; i < q; i++) {
            int x = sc.nextInt() - 1;
            int y = sc.nextInt() - 1;
            long dist = dijkstra(adj, x, k) + dijkstra(adj, k, y);
            System.out.println(dist);
        }
        sc.close();
    }

    static long dijkstra(List<List<Pair>> adj, int start, int end) {
        int n = adj.size();
        long[] dist = new long[n];
        Arrays.fill(dist, INF);
        dist[start] = 0;
        PriorityQueue<Pair> pq = new PriorityQueue<>(Comparator.comparingLong(p -> p.second));
        pq.offer(new Pair(start, 0));
        while (!pq.isEmpty()) {
            Pair curr = pq.poll();
            int u = curr.first;
            long d = curr.second;
            if (d > dist[u]) continue;
            for (Pair edge : adj.get(u)) {
                int v = edge.first;
                long w = edge.second;
                if (dist[u] + w < dist[v]) {
                    dist[v] = dist[u] + w;
                    pq.offer(new Pair(v, dist[v]));
                }
            }
        }
        return dist[end];
    }

    static class Pair {
        int first;
        long second;

        Pair(int first, long second) {
            this.first = first;
            this.second = second;
        }
    }
}

