#define MAXN 100005
#define INF 2e18

typedef struct {
    int to;
    long long cost;
} Edge;

int main() {
    int n;
    scanf("%d", &n);

    Edge edges[2 * (MAXN - 1)];
    int edgeCount = 0;
    for (int i = 0; i < n - 1; i++) {
        int u, v;
        long long c;
        scanf("%d %d %lld", &u, &v, &c);
        edges[edgeCount++] = (Edge){v, c};
        edges[edgeCount++] = (Edge){u, c};
    }

    long long dist[MAXN][MAXN];
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            dist[i][j] = INF;
        }
    }
    for (int i = 1; i <= n; i++) dist[i][i] = 0;

    for (int i = 0; i < edgeCount; i++) {
        dist[edges[i].to][edges[i].to] = 0;
        dist[edges[i].to][edges[i].to] = 0;
        dist[edges[i].to][edges[ (i+1)%edgeCount ].to] = edges[i].cost;
        dist[edges[ (i+1)%edgeCount ].to][edges[i].to] = edges[i].cost;
    }

    for (int k = 1; k <= n; k++) {
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                if (dist[i][k] != INF && dist[k][j] != INF) {
                    dist[i][j] = (dist[i][j] > dist[i][k] + dist[k][j]) ? dist[i][k] + dist[k][j] : dist[i][j];
                }
            }
        }
    }

    long long maxPath = 0;
    int p[n];
    for (int i = 0; i < n; i++) p[i] = i + 1;

    do {
        long long currentPath = 0;
        for (int i = 0; i < n - 1; i++) {
            currentPath += dist[p[i]][p[i + 1]];
        }
        maxPath = (maxPath > currentPath) ? maxPath : currentPath;
    } while (next_permutation(p, p + n));

    printf("%lld\n", maxPath);

    return 0;
}

import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<Edge> edges = new ArrayList<>();
        for (int i = 0; i < n - 1; i++) {
            int a = sc.nextInt();
            int b = sc.nextInt();
            int c = sc.nextInt();
            edges.add(new Edge(a, b, c));
        }
        sc.close();

        long[][] dist = new long[n + 1][n + 1];
        for (int i = 1; i <= n; i++) {
            Arrays.fill(dist[i], Long.MAX_VALUE);
            dist[i][i] = 0;
        }
        for (Edge edge : edges) {
            dist[edge.a][edge.b] = dist[edge.b][edge.a] = edge.c;
        }

        for (int k = 1; k <= n; k++) {
            for (int i = 1; i <= n; i++) {
                for (int j = 1; j <= n; j++) {
                    if (dist[i][k] != Long.MAX_VALUE && dist[k][j] != Long.MAX_VALUE) {
                        dist[i][j] = Math.min(dist[i][j], dist[i][k] + dist[k][j]);
                    }
                }
            }
        }

        long maxPath = 0;
        List<Integer> path = new ArrayList<>();
        for (int i = 1; i <= n; i++) {
            path.add(i);
        }
        do {
            long currentPath = 0;
            for (int i = 0; i < n - 1; i++) {
                currentPath += dist[path.get(i)][path.get(i + 1)];
            }
            maxPath = Math.max(maxPath, currentPath);
        } while (nextPermutation(path));

        System.out.println(maxPath);
    }

    static boolean nextPermutation(List<Integer> nums) {
        int i = nums.size() - 2;
        while (i >= 0 && nums.get(i) >= nums.get(i + 1)) {
            i--;
        }
        if (i < 0) {
            return false;
        }
        int j = nums.size() - 1;
        while (nums.get(j) <= nums.get(i)) {
            j--;
        }
        Collections.swap(nums, i, j);
        Collections.reverse(nums.subList(i + 1, nums.size()));
        return true;
    }

    static class Edge {
        int a, b, c;

        Edge(int a, int b, int c) {
            this.a = a;
            this.b = b;
            this.c = c;
        }
    }
}

import heapq

def solve():
    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v, w = map(int, input().split())
        edges.append((u, v, w))
        edges.append((v, u, w))

    graph = {i: [] for i in range(1, n + 1)}
    for u, v, w in edges:
        graph[u].append((v, w))

    dist = [[float('inf')] * (n + 1) for _ in range(n + 1)]
    for i in range(1, n + 1):
        dist[i][i] = 0
        pq = [(0, i)]
        while pq:
            d, u = heapq.heappop(pq)
            if d > dist[i][u]:
                continue
            for v, w in graph[u]:
                if dist[i][v] > dist[i][u] + w:
                    dist[i][v] = dist[i][u] + w
                    heapq.heappush(pq, (dist[i][v], v))

    complete_graph = {}
    for i in range(1, n + 1):
        for j in range(i + 1, n + 1):
            complete_graph[(i, j)] = dist[i][j]
            complete_graph[(j, i)] = dist[i][j]

    import itertools
    max_path_len = 0
    for path in itertools.permutations(range(1, n + 1)):
        current_path_len = 0
        for i in range(n - 1):
            current_path_len += complete_graph[(path[i], path[i+1])]
        max_path_len = max(max_path_len, current_path_len)
    print(max_path_len)

solve()

