public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        int[] b = new int[n];
        List<List<Integer>> adjA = new ArrayList<>();
        List<List<Integer>> adjB = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
            adjA.add(new ArrayList<>());
        }
        for (int i = 0; i < n; i++) {
            b[i] = sc.nextInt();
            adjB.add(new ArrayList<>());
        }
        for (int i = 0; i < n; i++) {
            if (a[i] != -1) {
                adjA[a[i] - 1].add(i);
            }
            if (b[i] != -1) {
                adjB[b[i] - 1].add(i);
            }
        }
        int[] x = new int[n];
        x[0] = 1;
        dfs(adjA, x, 0, 1);
        boolean possible = true;
        for (int i = 0; i < n; i++) {
            int sum = 0;
            Queue<Integer> q = new LinkedList<>();
            q.offer(i);
            List<Integer> descendants = new ArrayList<>();
            while (!q.isEmpty()) {
                int u = q.poll();
                descendants.add(u);
                for (int v : adjB.get(u)) {
                    q.offer(v);
                }
            }
            for (int u : descendants) {
                sum += x[u];
            }
            if (Math.abs(sum) != 1) {
                possible = false;
                break;
            }
        }
        if (possible) {
            System.out.println("POSSIBLE");
            for (int i = 0; i < n; i++) {
                System.out.print(x[i] + " ");
            }
            System.out.println();
        } else {
            System.out.println("IMPOSSIBLE");
        }
        sc.close();
    }
    static void dfs(List<List<Integer>> adj, int[] x, int u, int val) {
        x[u] = val;
        for (int v : adj.get(u)) {
            dfs(adj, x, v, -val);
        }
    }
}

