        color[0] = 1;
        color[n - 1] = -1;
        int blackCount = 1;
        int whiteCount = 1;
        boolean turn = true;
        while (true) {
            int u = -1;
            if (turn) {
                for (int i = 0; i < n; i++) {
                    if (color[i] == 0) {
                        for (int v : adj.get(i)) {
                            if (color[v] == 1) {
                                u = i;
                                break;
                            }
                        }
                    }
                    if (u != -1) break;
                }
            } else {
                for (int i = 0; i < n; i++) {
                    if (color[i] == 0) {
                        for (int v : adj.get(i)) {
                            if (color[v] == -1) {
                                u = i;
                                break;
                            }
                        }
                    }
                    if (u != -1) break;
                }
            }
            if (u == -1) break;
            color[u] = turn ? 1 : -1;
            if (turn) blackCount++;
            else whiteCount++;
            turn = !turn;
        }
        System.out.println(blackCount > whiteCount ? "Fennec" : "Snuke");
    }
}

