        for (int j = 0; j < n; j++) {
            for (int k = 0; k < n; k++) {
                adj[j][k] = 0;
            }
        }

        for (int j = 0; j < m; j++) {
            if (!((i >> j) & 1)) {
                adj[edges[j][0]][edges[j][1]] = adj[edges[j][1]][edges[j][0]] = 1;
            } else {
                current_cost += edges[j][2];
            }
        }

        int count = 0;
