        for (int j = 0; j < n; j++) visited[j] = 0;
        int q[n];
        int head = 0, tail = 1;
        q[0] = 0;
        visited[0] = 1;
        while (head < tail) {
            int u = q[head++];
            for (int v = 0; v < n; v++) {
                if (adj[u][v] && !visited[v]) {
                    visited[v] = 1;
                    q[tail++] = v;
                }
            }
        }

        int path_count = 0;
        int path[n];
        int path_len = 0;
        int visited_path[n];
        for(int k=0; k<n; ++k) visited_path[k] = 0;

        function(0,adj,n,path,&path_len,visited_path, &path_count);


        if (path_count == 1 && visited[n-1]) {
            min_cost = (min_cost < current_cost) ? min_cost : current_cost;
        }
    }

