}

void function(int u, int adj[][15], int n, int path[], int *path_len, int visited_path[], int* path_count){
    path[*path_len] = u;
    (*path_len)++;
    visited_path[u] = 1;

    if(u == n-1){
        (*path_count)++;
    } else {
        for(int v=0; v<n; ++v){
            if(adj[u][v] && !visited_path[v]){
                function(v,adj,n,path, path_len, visited_path, path_count);
            }
        }
    }

    (*path_len)--;
    visited_path[u] = 0;
}

import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<Edge> edges = new ArrayList<>();
        for (int i = 0; i < m; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            int c = sc.nextInt();
            edges.add(new Edge(u, v, c));
        }
        long minCost = Long.MAX_VALUE;
        for (int i = 0; i < (1 << m); i++) {
            long currentCost = 0;
            List<Edge> currentEdges = new ArrayList<>();
            for (int j = 0; j < m; j++) {
                if ((i >> j) % 2 == 0) {
                    currentEdges.add(edges.get(j));
                } else {
                    currentCost += edges.get(j).cost;
                }
            }
            if (isConnected(n, currentEdges) && isOnePath(n, currentEdges)) {
                minCost = Math.min(minCost, currentCost);
            }
        }
        System.out.println(minCost);
    }

    static boolean isConnected(int n, List<Edge> edges) {
        boolean[] visited = new boolean[n + 1];
        dfs(1, visited, edges);
        for (int i = 1; i <= n; i++) {
            if (!visited[i]) {
                return false;
            }
        }
        return true;
    }

    static void dfs(int u, boolean[] visited, List<Edge> edges) {
        visited[u] = true;
        for (Edge edge : edges) {
            if (edge.u == u && !visited[edge.v]) {
                dfs(edge.v, visited, edges);
            } else if (edge.v == u && !visited[edge.u]) {
                dfs(edge.u, visited, edges);
            }
        }
    }

    static boolean isOnePath(int n, List<Edge> edges) {
        if (edges.size() != n - 1) return false;
        boolean[] visited = new boolean[n+1];
        int count = 0;
        dfs2(1, n, visited, edges, count);
        return count == 1;

    }

    static int dfs2(int u, int n, boolean[] visited, List<Edge> edges, int count){
        visited[u] = true;
        if(u == n) count++;
        for (Edge edge : edges) {
            int v = (edge.u == u) ? edge.v : (edge.v == u ? edge.u : 0);
            if(v != 0 && !visited[v]){
                count = dfs2(v, n, visited.clone(), edges, count);
            }
        }
        return count;
    }


    static class Edge {
        int u, v, cost;

        Edge(int u, int v, int cost) {
            this.u = u;
            this.v = v;
            this.cost = cost;
        }
    }
}

