        visited = {1}
        paths = []

        while q:
            path = q.pop(0)
            last_node = path[-1]

            if last_node == n:
                paths.append(path)
                continue

            for neighbor in adj[last_node]:
                if neighbor not in visited:
                    new_path = path + [neighbor]
                    q.append(new_path)
                    visited = set(new_path)


        if len(paths) == 1:
            min_cost = min(min_cost, cost)

