#include <stdio.h>

#define MOD 1000000007

int main() {
    int N, M, K;
    scanf("%d %d %d", &N, &M, &K);
    int conditions[K][3];
    for (int i = 0; i < K; i++) {
        scanf("%d %d %d", &conditions[i][0], &conditions[i][1], &conditions[i][2]);
        conditions[i][0]--; 
        conditions[i][1]--; 
    }

    long long count = 0;
    long long paths[M][N];
    for (int i = 0; i < M; i++) {
        for (int j = 0; j < N; j++) {
            paths[i][j] = 0;
        }
    }


    long long total_paths = 1;
    for (int i = 0; i < M; ++i) {
        total_paths = (total_paths * (1LL << (N-1))) % MOD;
    }

    for (long long i = 0; i < total_paths; ++i) {
        long long temp = i;
        int valid = 1;
        for (int j = 0; j < M; ++j) {
            paths[j][0] = 1;
            for (int k = 0; k < N - 1; ++k) {
                int move = temp % 2;
                paths[j][k+1] = paths[j][k] + move;
                temp /= 2;
            }
        }

        for (int j = 0; j < N; ++j) {
            for (int k = 1; k < M; ++k) {
                if (paths[k][j] < paths[k-1][j]) {
                    valid = 0;
                    break;
                }
            }
            if (!valid) break;
        }

        for (int j = 0; j < K; ++j) {
            if ((paths[conditions[j][0]][conditions[j][1] + 1] - paths[conditions[j][0]][conditions[j][1]]) != conditions[j][2]) {
                valid = 0;
                break;
            }
        }
        if (valid) count++;
    }

    printf("%lld\n", count % MOD);
    return 0;
}

import java.util.*;

public class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int k = sc.nextInt();
        Map<Pair, Integer> conditions = new HashMap<>();
        for (int i = 0; i < k; i++) {
            int a = sc.nextInt();
            int b = sc.nextInt();
            int c = sc.nextInt();
            conditions.put(new Pair(a, b), c);
        }
        long count = 0;
        int[][] paths = new int[m][n];
        generatePaths(paths, 0, conditions, n, m, count);
        System.out.println(count % MOD);
    }

    static void generatePaths(int[][] paths, int row, Map<Pair, Integer> conditions, int n, int m, long count) {
        if (row == m) {
            if (isValid(paths, n, m)) {
                count++;
            }
            return;
        }
        paths[row][0] = 1;
        for (int i = 1; i < n; i++) {
            int prev = paths[row][i - 1];
            paths[row][i] = prev;
            generatePaths(paths, row + 1, conditions, n, m, count);
            paths[row][i] = prev + 1;
            generatePaths(paths, row + 1, conditions, n, m, count);
            paths[row][i] = prev;
        }

    }


    static boolean isValid(int[][] paths, int n, int m) {
        for (int j = 0; j < n; j++) {
            for (int i = 1; i < m; i++) {
                if (paths[i][j] < paths[i - 1][j]) {
                    return false;
                }
            }
        }
        return true;

    }

    static class Pair {
        int first;
        int second;

        Pair(int first, int second) {
            this.first = first;
            this.second = second;
        }

        @Override
        public boolean equals(Object o) {
            if (this == o) return true;
            if (o == null || getClass() != o.getClass()) return false;
            Pair pair = (Pair) o;
            return first == pair.first && second == pair.second;
        }

        @Override
        public int hashCode() {
            return Objects.hash(first, second);
        }
    }
}

