int main() {
  int n;
  cin >> n;

  vector<int> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
    a[i]--; 
  }

  queue<pair<int, int>> q;
  q.push({0, 0});

  vector<bool> visited(n, false);
  visited[0] = true;

  while (!q.empty()) {
    int u = q.front().first;
    int dist = q.front().second;
    q.pop();

    if (u == 1) {
      cout << dist << endl;
      return 0;
    }

    int v = a[u];
    if (!visited[v]) {
      visited[v] = true;
      q.push({v, dist + 1});
    }
  }

  cout << -1 << endl;
  return 0;
}

