    if (abs(n - m) > 1) {
        printf("0\n");
        return 0;
    }

    long long ans = 0;
    if (n == m) {
        ans = (2 * factorial(n) * factorial(m)) % MOD;
    } else if (n == m + 1) {
        ans = factorial(n) * factorial(m) % MOD;
    } else {
        ans = factorial(n) * factorial(m) % MOD;
    }

    printf("%lld\n", ans);
    return 0;
}

