int main() {
    int n;
    scanf("%d", &n);
    Point points[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld %lld", &points[i].x, &points[i].y);
    }

    long long cost = 0;
    if (n > 1) {
        long long min_x_diff = -1, min_y_diff = -1;
        int min_x_i = -1, min_y_i = -1;
        for (int i = 1; i < n; i++) {
            long long x_diff = abs(points[i].x - points[0].x);
            long long y_diff = abs(points[i].y - points[0].y);
            if (min_x_diff == -1 || x_diff < min_x_diff) {
                min_x_diff = x_diff;
                min_x_i = i;
            }
            if (min_y_diff == -1 || y_diff < min_y_diff) {
                min_y_diff = y_diff;
                min_y_i = i;
            }
        }
        cost += min(min_x_diff, min_y_diff);


        long long total_cost = cost;
        for (int i = 1; i < n; i++) {
            long long min_dist = -1;
            for (int j = 0; j < n; j++) {
                if (i == j) continue;
                long long dist = min(abs(points[i].x - points[j].x), abs(points[i].y - points[j].y));
                if (min_dist == -1 || dist < min_dist) {
                    min_dist = dist;
                }
            }
            total_cost += min_dist;

        }

        long long min_cost = -1;
        for (int i = 0; i < n; i++){
            long long cur_cost = 0;
            for (int j = 0; j < n; j++){
                if (i == j) continue;
                cur_cost += min(abs(points[i].x - points[j].x), abs(points[i].y - points[j].y));
            }
            if (min_cost == -1 || cur_cost < min_cost) min_cost = cur_cost;
        }
        printf("%lld\n", min_cost);
    } else {
        printf("0\n");
    }
    return 0;
}

