    towns = []
    for _ in range(n):
        x, y = map(int, input().split())
        towns.append((x, y))

    min_cost = float('inf')
    for i in range(1 << (n * (n - 1) // 2)):
        edges = []
        cost = 0
        k = i
        for u in range(n):
            for v in range(u + 1, n):
                if k % 2 == 1:
                    edges.append((u, v))
                    cost += min(abs(towns[u][0] - towns[v][0]), abs(towns[u][1] - towns[v][1]))
                k //= 2

        if len(edges) < n -1:
            continue

        parent = list(range(n))
        def find(i):
            if parent[i] == i:
                return i
            parent[i] = find(parent[i])
            return parent[i]

        def union(i, j):
            root_i = find(i)
            root_j = find(j)
            if root_i != root_j:
                parent[root_i] = root_j
                return True
            return False

        num_connected_components = n
        for u, v in edges:
            if union(u,v):
                num_connected_components -=1

        if num_connected_components == 1:
            min_cost = min(min_cost, cost)

    print(min_cost)

