        }

        System.out.println(ans.mod(BigInteger.valueOf(mod)));
    }

    public static BigInteger factorial(long n, long mod) {
        BigInteger fact = BigInteger.ONE;
        for (long i = 1; i <= n; i++) {
            fact = fact.multiply(BigInteger.valueOf(i)).mod(BigInteger.valueOf(mod));
        }
        return fact;
    }
}

MOD = 10**9 + 7

def factorial(n):
    fact = 1
    for i in range(1, n + 1):
        fact = (fact * i) % MOD
    return fact

def power(a, b):
    res = 1
    a %= MOD
    while b > 0:
        if b % 2 == 1:
            res = (res * a) % MOD
        a = (a * a) % MOD
        b //= 2
    return res

def inverse(n):
    return power(n, MOD - 2)

def combinations(n, k):
    if k > n:
        return 0
    num = factorial(n)
    den = (factorial(k) * factorial(n - k)) % MOD
    return (num * inverse(den)) % MOD

