#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

typedef struct {
    long long x, y;
} Point;

long long min(long long a, long long b) {
    return a < b ? a : b;
}

long long dist(Point p1, Point p2) {
    return min(abs(p1.x - p2.x), abs(p1.y - p2.y));
}

int get_parent(int parent[], int i) {
    if (parent[i] == i)
        return i;
    return parent[i] = get_parent(parent, parent[i]);
}

void unite_sets(int parent[], int rank[], int a, int b) {
    a = get_parent(parent, a);
    b = get_parent(parent, b);
    if (a != b) {
        if (rank[a] < rank[b])
            parent[a] = b;
        else if (rank[a] > rank[b])
            parent[b] = a;
        else {
            parent[b] = a;
            rank[a]++;
        }
    }
}

int main() {
    int n;
    scanf("%d", &n);
    Point points[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld %lld", &points[i].x, &points[i].y);
    }

    long long edges[n * (n - 1) / 2][3];
    int k = 0;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            edges[k][0] = dist(points[i], points[j]);
            edges[k][1] = i;
            edges[k][2] = j;
            k++;
        }
    }

    qsort(edges, k, sizeof(edges[0]), [](const void *a, const void *b) {
        return ((long long *)a)[0] - ((long long *)b)[0];
    });

    int parent[n];
    int rank[n];
    for (int i = 0; i < n; i++) {
        parent[i] = i;
        rank[i] = 0;
    }

    long long total_cost = 0;
    for (int i = 0; i < k; i++) {
        if (get_parent(parent, edges[i][1]) != get_parent(parent, edges[i][2])) {
            total_cost += edges[i][0];
            unite_sets(parent, rank, edges[i][1], edges[i][2]);
        }
    }
    printf("%lld\n", total_cost);
    return 0;
}

