                return i
            parent[i] = find(parent[i])
            return parent[i]
        def union(i,j):
            root_i = find(i)
            root_j = find(j)
            if root_i != root_j:
                parent[root_i] = root_j
                return True
            return False

        num_edges = 0
        for u, v in edges:
            if union(u,v):
                num_edges +=1

        if num_edges == n - 1:
            min_cost = min(min_cost, cost)

    print(min_cost)

