int solve() {
  string s;
  cin >> s;

  if (s.length() == 1) return 0;

  int min_ops = -1;
  for (char target_char = 'a'; target_char <= 'z'; ++target_char) {
    string current_s = s;
    int current_ops = 0;
    bool possible = true;

    while (true) {
      bool all_same = true;
      for (char c : current_s) {
        if (c != target_char) {
          all_same = false;
          break;
        }
      }
      if (all_same) break;

      string next_s = "";
      bool changed = false;
      for (int i = 0; i < current_s.length() -1; ++i) {
        if (current_s[i] == target_char || current_s[i+1] == target_char) {
          next_s += (current_s[i] == target_char) ? current_s[i] : current_s[i+1];
          changed = true;
        } else {
          possible = false;
          break;
        }
      }
      if (!possible) break;
      if (!changed) break;

      current_s = next_s;
      current_ops++;
    }
    if (possible) {
      if (min_ops == -1 || current_ops < min_ops) {
        min_ops = current_ops;
      }
    }
  }
  return min_ops;
}

