            minBalance = Math.min(minBalance, balance);
        }
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < -minBalance; i++) {
            sb.append('(');
        }
        sb.append(s);
        for (int i = 0; i < balance - minBalance; i++) {
            sb.append(')');
        }
        System.out.println(sb.toString());
    }
}

