            distinct_colors++;
          }
        }
        if (distinct_colors != get<2>(conditions[i])) {
          satisfied = false;
          break;
        }
      }
      if (satisfied) {
        count = (count + 1) % mod;
      }
      return;
    }

    for (int i = 1; i <= 3; ++i) {
      colors[k] = i;
      solve(k + 1);
    }
  };

