    visited[x][y] = 1;
    if (x == tx && y == ty) return 1;
    for (int i = 0; i < h; i++) {
        if (pond[i][y] != '.' && !visited[i][y]) {
            if (dfs(i, y)) return 1;
        }
    }
    for (int j = 0; j < w; j++) {
        if (pond[x][j] != '.' && !visited[x][j]) {
            if (dfs(x, j)) return 1;
        }
    }
    return 0;
}

