    cin >> h >> w;

    vector<string> a(h);
    for (int i = 0; i < h; ++i) {
        cin >> a[i];
    }

    int start_row, start_col, end_row, end_col;
    for (int i = 0; i < h; ++i) {
        for (int j = 0; j < w; ++j) {
            if (a[i][j] == 'S') {
                start_row = i;
                start_col = j;
            } else if (a[i][j] == 'T') {
                end_row = i;
                end_col = j;
            }
        }
    }

    vector<pair<int, int>> leaves;
    for (int i = 0; i < h; ++i) {
        for (int j = 0; j < w; ++j) {
            if (a[i][j] == 'o') {
                leaves.push_back({i, j});
            }
        }
    }

    int min_removed = -1;
    for (int i = 0; i < (1 << leaves.size()); ++i) {
        int removed_count = 0;
        vector<string> current_a = a;
        for (int j = 0; j < leaves.size(); ++j) {
            if ((i >> j) & 1) {
                current_a[leaves[j].first][leaves[j].second] = '.';
                removed_count++;
            }
        }

        queue<pair<int, int>> q;
        q.push({start_row, start_col});
        vector<vector<bool>> visited(h, vector<bool>(w, false));
        visited[start_row][start_col] = true;
        bool reached_end = false;

        while (!q.empty()) {
            int row = q.front().first;
            int col = q.front().second;
            q.pop();

            if (row == end_row && col == end_col) {
                reached_end = true;
                break;
            }

            for (int k = 0; k < h; ++k) {
                if (k != row && current_a[k][col] != '.' && !visited[k][col]) {
                    q.push({k, col});
                    visited[k][col] = true;
                }
            }
            for (int k = 0; k < w; ++k) {
                if (k != col && current_a[row][k] != '.' && !visited[row][k]) {
                    q.push({row, k});
                    visited[row][k] = true;
                }
            }
        }

        if (!reached_end) {
            if (min_removed == -1 || removed_count < min_removed) {
                min_removed = removed_count;
            }
        }
    }

    cout << min_removed << endl;

    return 0;
}

import java.util.*;
import java.lang.*;

class Main {
    static int H, W;
    static char[][] pond;
    static int[] dx = {0, 0, 1, -1};
    static int[] dy = {1, -1, 0, 0};
    static int sx, sy, tx, ty;

    static boolean isValid(int x, int y) {
        return x >= 0 && x < H && y >= 0 && y < W;
    }

    static int bfs(char[][] currentPond) {
        Queue<int[]> q = new LinkedList<>();
        q.offer(new int[]{sx, sy});
        boolean[][] visited = new boolean[H][W];
        visited[sx][sy] = true;
        int count = 0;

        while (!q.isEmpty()) {
            int[] curr = q.poll();
            int x = curr[0];
            int y = curr[1];

            if (x == tx && y == ty) return 0;

            for (int i = 0; i < 4; i++) {
                int nx = x + dx[i];
                int ny = y + dy[i];
                while (isValid(nx, ny)) {
                    if (currentPond[nx][ny] != '.') {
                        if (!visited[nx][ny]) {
                            visited[nx][ny] = true;
                            q.offer(new int[]{nx, ny});
                        }
                        break;
                    }
                    nx += dx[i];
                    ny += dy[i];
                }
            }
        }
        return 1;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        H = sc.nextInt();
        W = sc.nextInt();
        pond = new char[H][W];
        List<int[]> leaves = new ArrayList<>();

        for (int i = 0; i < H; i++) {
            String row = sc.next();
            for (int j = 0; j < W; j++) {
                pond[i][j] = row.charAt(j);
                if (pond[i][j] == 'S') {
                    sx = i;
                    sy = j;
                } else if (pond[i][j] == 'T') {
                    tx = i;
                    ty = j;
                } else if (pond[i][j] == 'o') {
                    leaves.add(new int[]{i, j});
                }
            }
        }

        int minRemoved = Integer.MAX_VALUE;
        for (int i = 0; i < (1 << leaves.size()); i++) {
            char[][] currentPond = new char[H][W];
            for (int r = 0; r < H; r++) {
                System.arraycopy(pond[r], 0, currentPond[r], 0, W);
            }
            int removedCount = 0;
            for (int j = 0; j < leaves.size(); j++) {
                if ((i >> j) % 2 == 1) {
                    currentPond[leaves.get(j)[0]][leaves.get(j)[1]] = '.';
                    removedCount++;
                }
            }
            if (bfs(currentPond) == 1) {
                minRemoved = Math.min(minRemoved, removedCount);
            }
        }

        if (minRemoved == Integer.MAX_VALUE) {
            System.out.println(-1);
        } else {
            System.out.println(minRemoved);
        }
    }
}

