            if grid[r][c] == 'S':
                start_row, start_col = r, c
            elif grid[r][c] == 'T':
                end_row, end_col = r, c
            elif grid[r][c] == 'o':
                leaves.append((r, c))

    def bfs(current_leaves):
        q = collections.deque([(start_row, start_col)])
        visited = {(start_row, start_col)}
        while q:
            row, col = q.popleft()
            if row == end_row and col == end_col:
                return True
            for nr, nc in leaves:
                if (nr, nc) in current_leaves and (nr, nc) not in visited and (nr == row or nc == col):
                    q.append((nr, nc))
                    visited.add((nr, nc))
        return False

    min_removed = float('inf')
    for i in range(1 << len(leaves)):
        current_leaves = set()
        removed_count = 0
        for j in range(len(leaves)):
            if (i >> j) & 1:
                current_leaves.add(leaves[j])
            else:
                removed_count +=1

        if not bfs(current_leaves):
            min_removed = min(min_removed, removed_count)

    if min_removed == float('inf'):
        print(-1)
    else:
        print(min_removed)

solve()

