public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        List<Edge>[] graph = new ArrayList[n + 1];
        for (int i = 1; i <= n; i++) {
            graph[i] = new ArrayList<>();
        }
        for (int i = 0; i < m; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            long w = sc.nextLong();
            graph[u].add(new Edge(v, w));
        }
        long[] dp = new long[n + 1];
        Arrays.fill(dp, Long.MIN_VALUE);
        dp[1] = 0;
        for (int i = 1; i <= n; i++) {
            if (dp[i] == Long.MIN_VALUE) continue;
            for (Edge edge : graph[i]) {
                if (dp[edge.to] < dp[i] + edge.weight) {
                    dp[edge.to] = dp[i] + edge.weight;
                }
            }
        }
        boolean cycle = false;
        for (int i = 1; i <= n; i++) {
            for (Edge edge : graph[i]) {
                if (dp[edge.to] < dp[i] + edge.weight) {
                    cycle = true;
                    break;
                }
            }
            if (cycle) break;
        }
        if (cycle) {
            System.out.println("inf");
        } else {
            System.out.println(dp[n]);
        }
    }

    static class Edge {
        int to;
        long weight;

        Edge(int to, long weight) {
            this.to = to;
            this.weight = weight;
        }
    }
}

