import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        List<Edge> blueEdges = new ArrayList<>();
        List<Edge> redEdges = new ArrayList<>();
        for (int i = 0; i < n - 1; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            blueEdges.add(new Edge(u, v));
        }
        for (int i = 0; i < n - 1; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            redEdges.add(new Edge(u, v));
        }
        if (n == 2) {
            System.out.println("YES");
            return;
        }
        if (check(blueEdges, redEdges)) {
            System.out.println("YES");
        } else {
            System.out.println("NO");
        }
    }

    static boolean check(List<Edge> blueEdges, List<Edge> redEdges) {
        int n = blueEdges.size() + 1;
        for (int i = 0; i < (1 << (n - 1)); i++) {
            List<Edge> currentRedEdges = new ArrayList<>();
            List<Edge> currentBlueEdges = new ArrayList<>(blueEdges);
            boolean possible = true;
            for (int j = 0; j < n - 1; j++) {
                if ((i >> j) % 2 == 1) {

                    boolean found = false;
                    for(int k=0; k<currentBlueEdges.size();++k){
                        Edge edge = currentBlueEdges.get(k);
                        if(isConnected(edge.u, edge.v, redEdges, currentBlueEdges, k)){
                            currentRedEdges.add(new Edge(edge.u, edge.v));
                            currentBlueEdges.remove(k);
                            found = true;
                            break;
                        }
                    }
                    if(!found) {
                        possible = false;
                        break;
                    }

                }
            }
            if (possible && currentRedEdges.size() == n-1) {
                boolean match = true;
                for(Edge e : redEdges){
                    boolean found = false;
                    for(Edge c : currentRedEdges){
                        if((e.u == c.u && e.v == c.v) || (e.u == c.v && e.v == c.u)){
                            found = true;
                            break;
                        }
                    }
                    if(!found) match = false;
                }
                if(match) return true;
            }
        }
        return false;
    }
    static boolean isConnected(int u, int v, List<Edge> redEdges, List<Edge> blueEdges, int index){
        List<Edge> curEdges = new ArrayList<>(blueEdges);
        curEdges.remove(index);
        curEdges.addAll(redEdges);

        Queue<Integer> q = new LinkedList<>();
        q.offer(u);
        Set<Integer> visited = new HashSet<>();
        visited.add(u);
        while(!q.isEmpty()){
            int cur = q.poll();
            for(Edge e : curEdges){
                if(e.u == cur && !visited.contains(e.v)){
                    visited.add(e.v);
                    q.offer(e.v);
                }else if(e.v == cur && !visited.contains(e.u)){
                    visited.add(e.u);
                    q.offer(e.u);
                }
            }
        }
        return visited.contains(v);
    }

    static class Edge {
        int u, v;

        Edge(int u, int v) {
            this.u = u;
            this.v = v;
        }
    }
}

