#include <math.h>

int main() {
    long long N, Q, A, B;
    scanf("%lld %lld %lld %lld", &N, &Q, &A, &B);

    long long x[Q];
    for (long long i = 0; i < Q; i++) {
        scanf("%lld", &x[i]);
    }

    long long dp[Q + 1][N + 1][N + 1];
    for (long long i = 0; i <= Q; i++) {
        for (long long j = 1; j <= N; j++) {
            for (long long k = 1; k <= N; k++) {
                dp[i][j][k] = -1;
            }
        }
    }

    dp[0][A][B] = 0;

    for (long long i = 0; i < Q; i++) {
        for (long long j = 1; j <= N; j++) {
            for (long long k = 1; k <= N; k++) {
                if (dp[i][j][k] == -1) continue;
                long long next = x[i];

                if (dp[i+1][next][k] == -1 || dp[i+1][next][k] > dp[i][j][k] + abs(j - next)) {
                    dp[i+1][next][k] = dp[i][j][k] + abs(j - next);
                }
                if (dp[i+1][j][next] == -1 || dp[i+1][j][next] > dp[i][j][k] + abs(k - next)) {
                    dp[i+1][j][next] = dp[i][j][k] + abs(k - next);
                }
            }
        }
    }

    long long min_time = -1;
    for (long long j = 1; j <= N; j++) {
        for (long long k = 1; k <= N; k++) {
            if (dp[Q][j][k] != -1) {
                if (min_time == -1 || dp[Q][j][k] < min_time) {
                    min_time = dp[Q][j][k];
                }
            }
        }
    }
    printf("%lld\n", min_time);
    return 0;
}

