int main() {
  int n, m;
  cin >> n >> m;

  vector<tuple<int, int>> edges;
  vector<vector<int>> adj(n);
  for (int i = 0; i < m; ++i) {
    int u, v;
    cin >> u >> v;
    --u; --v;
    edges.emplace_back(u, v);
    adj[u].push_back(v);
    adj[v].push_back(u);
  }

  vector<int> path;
  vector<bool> visited(n, false);
  function<bool(int)> dfs = [&](int u) {
    path.push_back(u);
    visited[u] = true;

    vector<int> neighbors;
    for (int v : adj[u]) {
      bool ok = true;
      for (int p : path) {
        if (p == v) ok = false;
      }
      if (ok) neighbors.push_back(v);
    }

    if (neighbors.empty()) return true;

    for (int v : neighbors) {
      if (dfs(v)) return true;
    }

    path.pop_back();
    visited[u] = false;
    return false;
  };

  for (int i = 0; i < n; ++i) {
    if (dfs(i)) break;
  }

  cout << path.size() << endl;
  for (int i = 0; i < path.size(); ++i) {
    cout << path[i] + 1 << (i == path.size() - 1 ? "" : " ");
  }
  cout << endl;

