        while (!q.isEmpty()) {
            int u = q.poll();
            boolean flag = true;
            for (int v : adj.get(u)) {
                if (!visited.contains(v)) {
                    visited.add(v);
                    path.add(v);
                    q.offer(v);
                    flag = false;
                    break;
                }
            }
            if (flag) {
                break;
            }
        }
        System.out.println(path.size());
        for (int i = 0; i < path.size(); i++) {
            System.out.print(path.get(i) + (i == path.size() - 1 ? "" : " "));
