public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        long n = sc.nextLong();
        long m = sc.nextLong();
        long mod = 1000000007;
        long[][] dp = new long[(int) (n + 1)][(int) (n + 1)];
        dp[0][0] = 1;
        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= n; j++) {
                if (i + 1 <= n) {
                    dp[i + 1][j] = (dp[i + 1][j] + dp[i][j]) % mod;
                }
                if (j + 1 <= n) {
                    dp[i][j + 1] = (dp[i][j + 1] + dp[i][j]) % mod;
                }
            }
        }
        long ans = 0;
        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= n; j++) {
                if (i + j <= n) {
                    long cur = dp[i][j];
                    long ways = 1;
                    for (int k = 0; k < m; k++) {
                        ways = (ways * (i + j + 2 * k + 2 -1)) % mod;
                        ways = (ways * modInverse(k + 1,mod)) % mod;
                    }
                    ans = (ans + cur * ways) % mod;

                }
            }
        }
        System.out.println(ans);
    }

    public static long modInverse(long a, long m) {
        long m0 = m;
        long y = 0, x = 1;
        if (m == 1) return 0;
        while (a > 1) {
            long q = a / m;
            long t = m;
            m = a % m;
            a = t;
            t = y;
            y = x - q * y;
            x = t;
        }
        if (x < 0) x += m0;
        return x;
    }
}

