int main() {
    int N, Q;
    scanf("%d", &N);
    long long A[N], B[N], C[N + 1], D[Q], E[Q];
    for (int i = 0; i < N; i++) {
        scanf("%lld %lld", &A[i], &B[i]);
    }
    for (int i = 0; i <= N; i++) {
        scanf("%lld", &C[i]);
    }
    scanf("%d", &Q);
    for (int i = 0; i < Q; i++) {
        scanf("%lld %lld", &D[i], &E[i]);
        long long Z[N + 1][2];
        for (int j = 0; j < N; j++) {
            Z[j][0] = A[j];
            Z[j][1] = B[j];
        }
        Z[N][0] = D[i];
        Z[N][1] = E[i];
        int max_score = -1;
        for (int j = 0; j < (1 << (N + 1)); j++) {
            int score = 0;
            long long used[N + 1];
            for (int k = 0; k <= N; k++) {
                used[k] = (j >> k) & 1 ? Z[k][0] : Z[k][1];
            }
            long long Y_copy[N+1];
            for(int k=0; k<=N; ++k) Y_copy[k] = C[k];
            int possible = 1;
            for(int k=0; k<=N; ++k){
                int found = 0;
                for(int l=0; l<=N; ++l){
                    if(used[k] <= Y_copy[l]){
                        Y_copy[l] = -1;
                        found = 1;
                        break;
                    }
                }
                if(!found) {possible = 0; break;}
            }

            if (possible) {
                int current_score = 0;
                for (int k = 0; k <= N; k++) {
                    if ((j >> k) & 1) current_score++;
                }
                max_score = max_score == -1 ? current_score : (current_score > max_score ? current_score : max_score);
            }
        }
        printf("%d\n", max_score);
    }
    return 0;
}

