  vector<string> v = s;
  v.push_back(t);
  sort(v.begin(), v.end());

  vector<string> patterns;
  function<void(string, int)> generate = [&](string current, int k) {
    if (k == n) {
      patterns.push_back(current);
      return;
    }
    for (char c = 'a'; c <= 'z'; ++c) {
        string next = current;
        next += c;
        generate(next, k + 1);
    }
  };

    if (n > 0) {
        string current = "";
        generate(current,0);
    } else {
        patterns.push_back("");
    }

  for (int i = 0; i < patterns.size(); ++i) {
